```javascript
//Returns a list containing the intersection of list1 and list2  
function intersection(list1,list2){
  let result =new  LinkedList();
  let visitedNodes = []; // Keep track of all the visited nodes
  let currentNode = list1.getHead().nextElement;

  //Traversing list1 and adding all unique nodes into the hash set
  while (currentNode != null){
    let value = currentNode.data;
    if (!(visitedNodes.includes(value))){
      visitedNodes.push(value); //Visiting currentNode for first time
    }
    currentNode = currentNode.nextElement;
  }

  let start = list2.getHead().nextElement;

  //console.log(visitedNodes);

  //Traversing list 2
  //Nodes which are already present in visitedNodes are added to result
  while (start != null){
    let val = start.data;
    if (visitedNodes.includes(val)){
      result.insertAtTail(val);
    }
    start = start.nextElement;
  }
  result.removeDuplicates(); 
  return result;
}

