```javascript
var Person = require('./Person');

class PersonBuilder {

    constructor(name) {
        this.name = name;
    }

    makeEmployee() {
        this.isEmployee = true;
        return this; // always keep retruning the self instance.
    }

    makeManager(hours=40) {
        this.isManager = true;
        this.hours = hours;
